

const enGreet = require('./en');
const tn = require('./tn');
const esGreet = require('./es');

function greet(language) {
    if (language === 'en') {
        enGreet();
    } else if (language === 'tn') {
        tn.greet();
    } else if (language === 'es') {
        esGreet();
    }
}

module.exports = greet;